import React from 'react';
import ReactDOM from 'react-dom';
import GlobalStyle from './styles/global';
import Routering from './Routes/routes';

// Importe createRoot do pacote react-dom/client
import { createRoot } from 'react-dom/client';

// Variável global para controlar se createRoot já foi chamado
let rootCreated = false;

const Logi = () => {
  return (
    <React.StrictMode>
      <GlobalStyle/>
      <Routering />
    </React.StrictMode>
  );
}

const renderLogin = () => {
  createRoot(document.getElementById('root')).render(<Login />);
  rootCreated = true;
}

// Verifique se createRoot já foi chamado antes de chamar novamente
if (!rootCreated) {
  // Se createRoot ainda não foi chamado, crie um novo root
  renderLogin();
} else {
  // Se createRoot já foi chamado, atualize o conteúdo
  // Atualize o conteúdo do root existente aqui, se necessário
  ReactDOM.unstable_batchedUpdates(() => {
    // Por exemplo:
    // ReactDOM.render(<Login />, document.getElementById('root'));
  });
}

export default Login;
