import React, { useState } from 'react';
import './LoginCadastro.css'
import user_icon from '../images/person.png';
import email_icon from '../images/email.png';
import password_icon from '../images/password.png';

function LoginCadastro() {
    const [action, setAction] = React.useState("Cadastro");

    return (
        React.createElement("div", { className: 'container' },
            React.createElement("div", { className: "header" },
                React.createElement("div", { className: "text" }, "Cadastro"),
                React.createElement("div", { className: "underline" })
            ),
            React.createElement("div", { className: "inputs" },
                React.createElement("div", { className: "input" },
                    React.createElement("img", { src: user_icon }),
                    React.createElement("input", { type: "text", placeholder: "Nome" })
                ),
                React.createElement("div", { className: "input" },
                    React.createElement("img", { src: email_icon }),
                    React.createElement("input", { type: "email", placeholder: "Email" })
                ),
                React.createElement("div", { className: "input" },
                    React.createElement("img", { src: password_icon }),
                    React.createElement("input", { type: "password", placeholder: "senha" })
                )
            ),
            React.createElement("div", { className: "forgot-password" },
                "Esqueceu sua senha? ", React.createElement("span", null, "Clique Aqui")
            ),
            React.createElement("div", { className: "submit-container" },
                React.createElement("div", { className: action === "Cadastro" ? "submit gray" : "submit" }, "Cadastro"),
                React.createElement("div", { className: action === "Login" ? "submit gray" : "submit" }, "Login")
            )
        )
    );
}

export default LoginCadastro;
